			quest->achievement_quest_typeid = rs["achievement_quest_typeid"];
			quest->counter_id = rs["counter_id"];
			quest->success_date = !rs.isNull("success_date") ? (Poco::DateTime)rs["success_date"] : Poco::DateTime();
